import React from 'react'
import './css/article.css'
import { Link } from 'react-router-dom'

class FullArticle extends React.Component {
    state = {
      isLoading: true,
      users: [],
      tag:'',
      error: null
    };

    fetchUsers() {

      fetch(`http://localhost:8000/api/polls`)
        .then(response => response.json())
        .then(data =>
          this.setState({
            users: data,
            tag:data.tag,
            isLoading: false,
          })
        )
        .catch(error => this.setState({ error, isLoading: false }));
    }

    componentDidMount() {
      this.fetchUsers();
    }
    render() {

      const { isLoading, users,tag, error } = this.state;
      return (
        <React.Fragment>

          {error ? <p>{error.message}</p> : null}
          {!isLoading ? (
            users.map(user => {
              if (tag==='poem') 

              return (
                <div className="params">
                <h1 className="entry_title">
                <a>{user.title}</a>
                </h1>

                <div className="entry_content">



    <li key="user.id">
      <Link to={`/article/${user.id}`}>continue reading</Link>
    </li>


                  </div>
                </div>  

              )
              else 
              {return <div><h1>{user.title}</h1></div>}


              ;
            })
          ) : (
            <h3> loading ....</h3>
          )}
        </React.Fragment>
      );
    }
  }

export default FullArticle
