    this.state = {
      articleTitle: '',
      artcontent:''
    }
  }
 componentDidMount(){
  const id=this.props.match.params.id;
  
  axios.get (
   `https://neunhuladinhmenh.herokuapp.com/api/polls/${id}`
 )
 
 .then(response => {
   if (response.data.tag==null){
