import React from 'react'



class Header extends React.Component {
  constructor() {
    super();
    this.state = {showMe: true
    }

  }

  operation() {
    this.setState ({
      showMe: !this.state.showMe
    })
  }

  render() {
    return (
      <div>
        <button onClick={()=>this.operation()}>
         click me
        </button>
        <h1>Hide and show in React js </h1>{
          this.state.showMe?
          <div>
            please hide me
         </div>:null        }
      </div>
    );
  }
}



  export default Header
