import React from 'react'
//import {batmi} from '../actions/authentication';
class About extends React.Component {
    constructor(){super();
        let posts_str = localStorage.getItem('secret');
        const posts_arr=[posts_str];
        //const posts=JSON.parse(posts_arr);
        this.state = {posts:posts_str,
            isLoading:false
        };
      }


    render(){ 
        const {posts,isLoading}=this.state
        return(
            <React.Fragment>
                          {!isLoading ? (
        posts.split("\r\n").map(post=>{
     return  (<div>
        <h1 key='post.title'>{post.title}</h1>

        {post.content}
       </div>
                 )

  }
         )
        ) : (
            <h3> loading ....</h3>
          )}
        </React.Fragment>)
}
}
export  default About
