        const content=posts_arr['content'];

        this.state = {posts:content,

            isLoading:false
        };
      }


    render(){ 
        const {posts,isLoading}=this.state
        return(
            <React.Fragment>
                          {!isLoading ? (
        posts.split("\r\n").map(posts=>{ 

     return  (<div>
        <h1 key='posts.title'>{posts.title}</h1>
{posts.content}

       </div>
                 )

  }
         )
