import React from 'react'


const entry_title={


    display:block

}
const entry_content={

fontfamily:"ff-meta-serif-web-pro",
}
class Article extends React.Component {
  state = {
    isLoading: true,
    users: [],
    error: null
  };

  fetchUsers() {
    fetch(`https://neunhuladinhmenh.herokuapp.com/api/polls`)
      .then(response => response.json())
      .then(data =>
        this.setState({
          users: data,
          isLoading: false,
        })
      )
      .catch(error => this.setState({ error, isLoading: false }));
  }

  componentDidMount() {
    this.fetchUsers();
  }
  render() {
    const { isLoading, users, error } = this.state;
    return (
      <React.Fragment>

        {error ? <p>{error.message}</p> : null}
        {!isLoading ? (
          users.map(user => {
            const {  title, content } = user;
            return (
              <div>
              <h1 style={entry_title}>
              <a>{title}</a>
              </h1>

              <div style={entry_content}>
                <p> {content}</p>
                <a href="">Continue reading</a>
                </div>
              </div>  

            );
          })
        ) : (
          <h3>Loading...</h3>
        )}
      </React.Fragment>
    );
  }
}





export default Article
