async componentDidMount () {
  const id = this.props.match.params.id
  const characterNames = [];

  const {articleContent} = await axios.get (
    `https://neunhuladinhmenh.herokuapp.com/api/polls/${id}`
  )
  const charactersFetchLinks = articleContent.data.results[0].title.slice (
    0,
    10
  );
  const promisesData = await charactersFetchLinks.map (link =>
    axios.get (link)
  );
  axios.all (promisesData).then (value => {
    value.map (val => characterNames.push (val.data.title));
  const {id,title,content}=articleContent.data.results[0];
  this.setState ({
    id,
    title: characterNames,
    content,
    loading: false
  });

