//import {batmi} from '../actions/authentication';
class About extends React.Component {
    constructor(){super();
        let posts = localStorage.getItem('secret');
        this.state = {posts:[posts],
            isLoading:false
        };
      }


    render(){ 
        const {posts,isLoading}=this.state
        return(
            <React.Fragment>
                          {!isLoading ? (
         posts.map(post=>{
     return  (<div>
        <h1 key='post.title'>{post[1]}</h1>

        {post[2]}
       </div>
                 )

  }
         )
        ) : (
            <h3> loading ....</h3>
          )}
        </React.Fragment>)
}
}
export  default About
