import React from 'react'
//import {batmi} from '../actions/authentication';
class About extends React.Component {
    constructor(){super();
        let posts_str = (localStorage.getItem('secret'));
       // let post=JSON.parse(posts_str);

        this.state = {
             posts:[posts_str],
            isLoading:false
        };
      }


    render(){ 
        const {isLoading,posts}=this.state
        return(
            <React.Fragment>
            {!isLoading ? (
              posts.map(posts=>{ 

            return  (<div>
              <h1 key='posts["title"]'>{posts["title"]}</h1>
              {posts["content"]}
             </div>
                       )

            }
               )
              ) : (
                  <h3> loading ....</h3>
                )}

        </React.Fragment>)
}
}
export  default About
