import { SECRET_POST } from './actions/types';
import isEmpty from '../validation/is-empty';

const initialState = {
    isAuthenticated: false,
    post: {}
}

export default function(state = initialState, action ) {
    switch(action.type) {
        case SECRET_POST:
            return {
                ...state,
                isAuthenticated: !isEmpty(action.payload),
                user: action.payload
            }
        default: 
            return state;
    }
}
