async componentDidMount () {
  const characterNames = [];


  const articleContent = await axios.get (
    `https://neunhuladinhmenh.herokuapp.com/api/polls/${encodeURI ( this.props.match.params.id)}`
  )
  const charactersFetchLinks = articleContent.data.results[0].users.slice (
    0,
    10
  );
  console.log(charactersFetchLinks)
  const promisesData = await charactersFetchLinks.map (link =>
    axios.get (link)
  );
  axios.all (promisesData).then (value => {
    value.map (val => characterNames.push (val.data.title));

    this.setState ({

      users: characterNames,
      loading: false,
    });
  });
  console.log(this.state.users);
}


